    def valnum(self):
        if self.ent_number.text() != "":
            if int(self.ent_number.text()) > 30:
                self.senha_gerada.setStyleSheet(stylesheet_error)
                self.senha_gerada.setText("Senha com muitos caracteres, escolha entre 1 a 30")
            elif int(self.ent_number.text()) <= 0:
                self.senha_gerada.setStyleSheet(stylesheet_error)
                self.senha_gerada.setText("Entre com um numero maior que 0")
            else:
                self.senha_gerada.setStyleSheet(stylesheet_gerou)
                response = requests.get(f'https://gerador-de-senha.onrender.com/password?length={str(self.ent_number.text())}')
                if response.status_code == 200:
                    data = response.json()
                    senha = data["password"]
                    self.senha_gerada.setText(senha)

    def my_github(self):
        url = "https://github.com/MateusSanchesRodriguez"
        webbrowser.open(url)

